INSERT INTO `tb_article` VALUES ('22', '本站已经完成初步制作', '南广轩主', '本站追求简约，大气的风格，经过两周的制作，已经完成前台和后台的基本功能，其他内容后续会分布更新，敬请期待！', '本站追求简约，大气的风格，经过两周的制作，已经完成前台和后台的基本功能，其他内容后续会分布更新，敬请期待！', './Public/Uploads/2017-12-10/thumb_15129151521856755913.png', '1', '1', '1', '1505218145');
INSERT INTO `tb_article` VALUES ('27', 'WEB前端学习路线简介', '南广轩主', '简单列举学习前端开发线路的提纲，供新手入门参考。', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px;&quot;&gt;一、WEB应用程序的工作原理&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px;&quot;&gt;&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px;&quot;&gt;浏览器和服务器之间如何交换信息的，HTTP协议、静态网页和动态网页的概念，网页编程工具选择等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;二、&lt;/strong&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;HTML基础&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;html的基本语法，常用标签的属性和值，表单等等。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;三、CSS基础&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;/strong&gt;在html中引入css的几种方式，css基本语法，选择器，属性单位，样式继承，样式层叠等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;四、CSS页面基本布局&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;/strong&gt;盒模型，文档流布局，元素浮动，元素定位，浏览器兼容解决方法等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;PS:到这里常见的页面布局都能够知道如何排版啦！&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;五、JavaScript基础语法&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;/strong&gt;引入js到html中的方式，js变量，js数据类型，表达式和运算符，语句等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;六、&lt;/strong&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;JavaScript提升&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;/strong&gt;js中的对象，数组，函数，正则表达式的简单使用等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;七、&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;JavaScript高级&lt;/strong&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;/strong&gt;&lt;/strong&gt;js事件模型，BOM，DOM等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;八、jQuery类库的使用&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;/strong&gt;jq选择器，ajax，动画效果等等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;学到这里，web前端必须的基础知识就学得差不多啦！恭喜你可以开发常见的网站了！&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;但是最近几年来，各种优秀的前端框架、库层出不穷，还需要多多了解学习，下面列举几个热门前端框架：&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;Bootstrap、Vue.js、Argular.js 等等&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: 14px; line-height: 21px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', './Public/Uploads/2017-12-10/thumb_15129151521856755913.png', '2', '1', '3', '1505819224');
INSERT INTO `tb_article` VALUES ('28', '网站ICP备案的好处', '南广轩主', '经过几天的资料准备和十几天的等待，本站ICP备案已经通过啦！', '<p>\n	<b><span style=\"font-size:18px;\">为自己的网站备案的好处有以下几点：</span></b> \n</p>\n<p>\n	<b><br />\n</b> \n</p>\n<p>\n	<strong>1、网站备案之后就可以使用国内的服务器啦，访问速度得到提升！</strong> \n</p>\n<p>\n	<strong>2、有利于搜索引擎的收录（SEO）。</strong> \n</p>\n<p>\n	<strong>3、很多站长会和已备案的网站站长交换友情链接。</strong> \n</p>\n<p>\n	<strong>4、积极相应国家政策的好公民。</strong> \n</p>\n<p>\n	<strong>5、...</strong> \n</p>', './Public/Uploads/2017-12-10/thumb_15129151521856755913.png', '2', '1', '4', '1505812065');
INSERT INTO `tb_article` VALUES ('30', 'CSS代码重置示例', '南广轩主', '最近用到的CSS重置的代码分享', '<p>\n	<strong>CSS重置的主要原因是：</strong> \n</p>\n<p>\n	<strong><br />\n</strong> \n</p>\n<p>\n	1、在不同平台和不同浏览器上浏览同一个网页会有兼容性问题，重置CSS能够避免一些不同浏览器上布局混乱的问题；\n</p>\n<p>\n	<br />\n</p>\n<p>\n	2、CSS重置后，开发者在编写网页的时候只需要添加样式而不需修改和删除；\n</p>\n<p>\n	<br />\n</p>\n<p>\n	3、CSS重置代码和开发者自定义修饰都放在公共样式表文件里，避免重复书写；\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<strong>如何重置CSS样式：</strong> \n</p>\n<p>\n	<strong><br />\n</strong> \n</p>\n<p>\n	<strong> </strong>1、首先一般都是用*{margin: 0;padding: 0;}来重置内外边距，这样书写简单，只是效率不高，因为有的标签本身就没有这些属性；\n</p>\n<p>\n	<br />\n</p>\n<p>\n	2、对ul、li、a、img的重置\n</p>\n<p>\n	<img src=\"/Public/upload/image/20170920/20170920233911_62558.png\" alt=\"\" /> \n</p>\n<p>\n	<br />\n</p>\n<p>\n	3、对常用的左右浮动和居中等定制统一的CSS修饰\n</p>\n<p>\n	<img src=\"/Public/upload/image/20170920/20170920233040_81581.png\" alt=\"\" /> \n</p>\n<p>\n	<img src=\"/Public/upload/image/20170920/20170920233057_57581.png\" alt=\"\" /> \n</p>\n<p>\n	<br />\n</p>\n<p>\n	4、对按钮的重置和自定义统一样式\n</p>\n<p>\n	<img src=\"/Public/upload/image/20170920/20170920233213_94913.png\" alt=\"\" /> \n</p>\n<p>\n	<br />\n</p>\n<p>\n	5、对全网站公共的修饰，如导航和脚部都可以放在公共CSS样式表中\n</p>\n<p>\n	<img src=\"/Public/upload/image/20170920/20170920233410_60339.png\" alt=\"\" /> \n</p>\n<p>\n	<br />\n</p>\n<p>\n	<strong>附：</strong> \n</p>\n<p>\n	<strong><br />\n</strong> \n</p>\n<blockquote>\n	<blockquote>\n		<p>\n			*{margin: 0;padding: 0;}\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			body{font-family: \"微软雅黑\";}\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			ul,li,ol{list-style: none;}\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			a{text-decoration: none;color: #000;cursor: pointer;}\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			img{border: none;vertical-align: middle;}\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			/*浮动*/\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			.floatl{float: left;}\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			.floatr{float: right;}\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			/*title*/\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			h1,h2,h3,h4,h5,h6 {margin:0;padding:0;font-size:14px;}\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			<br />\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			/*居中*/\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			.container{width: 1050px;margin: 0 auto;}\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			/*按钮*/\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			input{border: none;cursor: pointer;}\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			/*白色按钮*/\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			.btn-white{background-color: #fff;color: #000;border-radius: 5px;border: 1px darkgray solid;}\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			.btn-white:hover{opacity: 0.8;}\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			/*大按钮*/\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			.btn-big{padding: 10px 15px;font-size: 22px;}\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			/*中按钮*/\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			.btn-mid{padding: 5px 7.5px;}\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			<br />\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			<br />\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			/*以下为顶部和底部通用样式*/\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			/*nav*/\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			.navBoxbg{min-width:1050px;background: #000;height: 50px;}\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			.navBox{overflow: hidden;}\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			.nav ul li{float: left;}\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			.logo a,.nav ul li a{display: block;height: 50px;width: 80px;color:#fff;text-align: center;line-height: 50px;}\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			/*nav活动样式*/\n		</p>\n	</blockquote>\n	<blockquote>\n		<p>\n			.navActive{background: #333333;}\n		</p>\n	</blockquote>\n</blockquote>\n<p>\n	<br />\n</p>', './Public/Uploads/2017-12-10/thumb_15129151521856755913.png', '2', '1', '3', '1505916585');
INSERT INTO `tb_article` VALUES ('33', 'CSS如何命名的规范示例', '南广轩主', 'CSS如何命名的规范示例', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;每一个开发者可能都有自己的命名方式，但是在新手刚学习的时候可能会对CSS选择器命名有点不知所措，比如说我在刚开始学的时候，内容多&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;一些的半个页面，我就词穷了，在这里给大家分享一些CSS选择器的命名示例。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;首先我们来看一下规范CSS的选择器命名有哪些好习惯值得注意：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;1、命名一般采用&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;见名知意&lt;/strong&gt;的方式，即导航使用nav,内容使用content,页脚使用footer等，让阅读者更快的明白代码含义；&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;2、名字应采用常见的英文单词，做到&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;通俗易懂&lt;/strong&gt;；&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;3、在适当的地方一定要&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;书写备注&lt;/strong&gt;；&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;4、二级类名使用&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;驼峰命名法&lt;/strong&gt;（即第一个单词以小写字母开始；第二个单词的首字母大写或每一个单词的首字母都采用大写字母）；&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;下面举一些例子：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;1、CSS文件命名：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;主要的 master.css&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; line-height: 1.5;&quot;&gt;全局：global.css&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; line-height: 1.5;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; line-height: 1.5;&quot;&gt;模块 module.css&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;margin: 0px; padding: 0px; line-height: 1.5;&quot;&gt;基本共用 base.css&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; line-height: 1.5;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;布局，版面 layout.css&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; line-height: 1.5;&quot;&gt;主题 themes.css&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; line-height: 1.5;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;专栏 columns.css&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; line-height: 1.5;&quot;&gt;文字 font.css&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; line-height: 1.5;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;表单 forms.css&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; line-height: 1.5;&quot;&gt;补丁 mend.css&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; line-height: 1.5;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;打印 print.css&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;2、页面结构命名：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;容器: container&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 页头：header&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;内容：content/container&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 页面主体：main&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;页尾：footer&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 导航：nav&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;侧栏：sidebar&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 栏目：column&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; font-size: medium; text-indent: 32px; white-space: normal;&quot;&gt;页面外围控制整&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 左右中：left right cen